  calc(cal,'caller.' + attribute);
  setinterval(calc('merc_' + caller.name), get(time), 
	if(vverh,tween(get(cal),get(max),get(time)); set(vverh,false);
			,
			tween(get(cal),get(min),get(time)); set(vverh,true);
		);
	);



